    markup_items = types.ReplyKeyboardMarkup(one_time_keyboard=True, resize_keyboard=True, row_width=1)
    item_a = types.InlineKeyboardButton(text='–û—Ç–ø—Ä–∞–≤–ª—é—Å—å –≤ –ø—É—Ç–µ—à–µ—Å—Ç–≤–∏–µ')
